exposeGetterAndSetter(globalThis,
                      'DOMException',
                      () => {
                        const DOMException = lazyDOMExceptionClass();
                        exposeInterface(globalThis, 'DOMException', DOMException);
                        return DOMException;
                      },
                      (value) => {
                        exposeInterface(globalThis, 'DOMException', value);
                      });

